HTMLElement.prototype.attr=function(a){return new function(a,b){this.element=a;this.name=b;this.has=function(){return this.element.hasAttribute(this.name)};this.remove=function(){return this.element.removeAttribute(this.name)};this.set=function(a){return this.element.setAttribute(this.name,a)};this.get=function(){return this.element.getAttribute(this.name,!1)};this.toggle=function(){this.has()?this.remove():this.set(!0)}}(this,a)};
